--
-- Name: get_doctor_inventory(character varying, character varying, boolean); Type: FUNCTION; Schema: chatbot; Owner: -
CREATE FUNCTION chatbot.get_doctor_inventory(p_client_id character varying, p_user_id character varying, p_is_admin boolean DEFAULT false) RETURNS TABLE(sku character varying, description text, brand character varying, content character varying, available_quantity integer, price numeric)
    LANGUAGE plpgsql STABLE SECURITY DEFINER
    SET search_path TO 'public'
    AS $$
BEGIN
  IF NOT p_is_admin THEN
    IF NOT EXISTS (
      SELECT 1 FROM clients c
      WHERE c.client_id = p_client_id AND c.user_id = p_user_id
    ) THEN
      RETURN;
    END IF;
  END IF;

  RETURN QUERY
  SELECT
    m.sku,
    m.description,
    m.brand,
    m.content,
    ib.available_quantity,
    m.price
  FROM cabinet_inventory ib
  JOIN medications m ON m.sku = ib.sku
  WHERE ib.client_id = p_client_id
    AND ib.available_quantity > 0
  ORDER BY m.brand, m.sku;
END;
$$;


--
-- Name: get_doctor_movements(character varying, character varying, boolean, text, integer); Type: FUNCTION; Schema: chatbot; Owner: -
CREATE FUNCTION chatbot.get_doctor_movements(p_client_id character varying, p_user_id character varying, p_is_admin boolean DEFAULT false, p_source text DEFAULT 'ambos'::text, p_limit_count integer DEFAULT 30) RETURNS TABLE(fuente text, sku character varying, description text, brand character varying, type text, quantity integer, price numeric, date timestamp with time zone)
    LANGUAGE plpgsql STABLE SECURITY DEFINER
    SET search_path TO 'public'
    AS $$
BEGIN
  IF NOT p_is_admin THEN
    IF NOT EXISTS (
      SELECT 1 FROM clients c
      WHERE c.client_id = p_client_id AND c.user_id = p_user_id
    ) THEN
      RETURN;
    END IF;
  END IF;

  RETURN QUERY
  SELECT * FROM (
    SELECT
      'botiquin'::TEXT AS f_fuente,
      mi.sku::VARCHAR AS f_sku,
      m.description::TEXT AS f_desc,
      m.brand::VARCHAR AS f_marca,
      mi.type::TEXT AS f_tipo,
      mi.quantity AS f_cant,
      mi.unit_price AS f_precio,
      mi.movement_date AS f_fecha
    FROM inventory_movements mi
    JOIN medications m ON m.sku = mi.sku
    WHERE mi.client_id = p_client_id
      AND (p_source = 'ambos' OR p_source = 'botiquin')

    UNION ALL

    SELECT
      'odv'::TEXT,
      vo.sku::VARCHAR,
      m.description::TEXT,
      m.brand::VARCHAR,
      'SALE_ODV'::TEXT,
      vo.quantity,
      vo.price,
      vo.date::TIMESTAMPTZ
    FROM odv_sales vo
    JOIN medications m ON m.sku = vo.sku
    WHERE vo.client_id = p_client_id
      AND (p_source = 'ambos' OR p_source = 'odv')
  ) sub
  ORDER BY 8 DESC
  LIMIT LEAST(p_limit_count, 100);
END;
$$;


--
-- Name: get_medication_prices(text, character varying); Type: FUNCTION; Schema: chatbot; Owner: -
CREATE FUNCTION chatbot.get_medication_prices(p_search_term text DEFAULT NULL::text, p_brand_filter character varying DEFAULT NULL::character varying) RETURNS TABLE(sku character varying, description text, brand character varying, content character varying, price numeric, last_updated timestamp with time zone)
    LANGUAGE plpgsql STABLE SECURITY DEFINER
    SET search_path TO 'public'
    AS $$
BEGIN
  IF p_search_term IS NOT NULL THEN
    RETURN QUERY
    SELECT
      m.sku,
      m.description,
      m.brand,
      m.content,
      m.price,
      m.last_updated
    FROM medications m
    WHERE (p_brand_filter IS NULL OR m.brand ILIKE p_brand_filter)
      AND (
        extensions.similarity(unaccent(lower(m.description)), unaccent(lower(p_search_term))) > 0.15
        OR extensions.similarity(m.sku, upper(p_search_term)) > 0.3
        OR m.sku ILIKE '%' || p_search_term || '%'
        OR m.description ILIKE '%' || p_search_term || '%'
      )
    ORDER BY GREATEST(
      extensions.similarity(unaccent(lower(m.description)), unaccent(lower(p_search_term))),
      extensions.similarity(m.sku, upper(p_search_term))
    ) DESC
    LIMIT 10;
  ELSE
    RETURN QUERY
    SELECT
      m.sku,
      m.description,
      m.brand,
      m.content,
      m.price,
      m.last_updated
    FROM medications m
    WHERE (p_brand_filter IS NULL OR m.brand ILIKE p_brand_filter)
    ORDER BY m.brand, m.sku
    LIMIT 50;
  END IF;
END;
$$;


--
-- Name: get_remaining_queries(character varying, character varying); Type: FUNCTION; Schema: chatbot; Owner: -
CREATE FUNCTION chatbot.get_remaining_queries(p_user_id character varying, p_role character varying) RETURNS TABLE(queries_used integer, queries_limit integer, remaining integer)
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$
DECLARE
  v_limit INTEGER;
  v_used INTEGER;
BEGIN
  IF p_role = 'OWNER' THEN
    v_limit := 30;
  ELSIF p_role = 'ADMIN' THEN
    v_limit := 10;
  ELSE
    v_limit := 5;
  END IF;

  SELECT COALESCE(ul.queries_used, 0)
  INTO v_used
  FROM chatbot.usage_limits ul
  WHERE ul.user_id = p_user_id
    AND ul.date = CURRENT_DATE;

  IF NOT FOUND THEN
    v_used := 0;
  END IF;

  RETURN QUERY SELECT v_used::INTEGER, v_limit::INTEGER, GREATEST(v_limit - v_used, 0)::INTEGER;
END;
$$;


