-- Name: get_user_collections(character varying, character varying, integer, boolean); Type: FUNCTION; Schema: chatbot; Owner: -
CREATE FUNCTION chatbot.get_user_collections(p_user_id character varying, p_client_id character varying DEFAULT NULL::character varying, p_limit integer DEFAULT 20, p_is_admin boolean DEFAULT false) RETURNS TABLE(collection_id uuid, client_id character varying, client_name character varying, status text, created_at timestamp with time zone, delivered_at timestamp with time zone, cedis_observations text, items json)
    LANGUAGE sql STABLE SECURITY DEFINER
    AS $$
  SELECT r.collection_id, r.client_id, c.client_name,
    r.status, r.created_at, r.delivered_at,
    r.cedis_observations,
    (SELECT COALESCE(json_agg(json_build_object(
      'sku', ri.sku, 'quantity', ri.quantity,
      'product', m.description
    )), '[]'::json)
    FROM collection_items ri
    LEFT JOIN medications m ON m.sku = ri.sku
    WHERE ri.collection_id = r.collection_id) as items
  FROM collections r
  JOIN clients c ON c.client_id = r.client_id
  WHERE (p_is_admin OR r.user_id = p_user_id)
    AND (p_client_id IS NULL OR r.client_id = p_client_id)
  ORDER BY r.created_at DESC
  LIMIT p_limit;
$$;


