--
-- Name: match_data_sheets(extensions.vector, double precision, integer); Type: FUNCTION; Schema: chatbot; Owner: -
CREATE FUNCTION chatbot.match_data_sheets(query_embedding extensions.vector, match_threshold double precision DEFAULT 0.70, match_count integer DEFAULT 5) RETURNS TABLE(sku character varying, content text, chunk_index integer, similarity double precision)
    LANGUAGE sql STABLE SECURITY DEFINER
    AS $$
  SELECT fc.sku, fc.content, fc.chunk_index,
    1 - (fc.embedding <=> query_embedding) AS similarity
  FROM chatbot.data_sheet_chunks fc
  WHERE 1 - (fc.embedding <=> query_embedding) > match_threshold
  ORDER BY fc.embedding <=> query_embedding ASC
  LIMIT LEAST(match_count, 20);
$$;


--
-- Name: match_medications(extensions.vector, double precision, integer); Type: FUNCTION; Schema: chatbot; Owner: -
CREATE FUNCTION chatbot.match_medications(query_embedding extensions.vector, match_threshold double precision DEFAULT 0.65, match_count integer DEFAULT 10) RETURNS TABLE(sku character varying, brand character varying, description text, content character varying, price numeric, conditions text, similarity double precision)
    LANGUAGE sql STABLE SECURITY DEFINER
    AS $$
  SELECT m.sku, m.brand, m.description, m.content, m.price,
    string_agg(DISTINCT p.name, ', ') as conditions,
    1 - (me.embedding <=> query_embedding) AS similarity
  FROM chatbot.medication_embeddings me
  JOIN medications m ON m.sku = me.sku
  LEFT JOIN medication_conditions mp ON mp.sku = m.sku
  LEFT JOIN conditions p ON p.condition_id = mp.condition_id
  WHERE 1 - (me.embedding <=> query_embedding) > match_threshold
  GROUP BY m.sku, m.brand, m.description, m.content, m.price, me.embedding, query_embedding
  ORDER BY me.embedding <=> query_embedding ASC
  LIMIT LEAST(match_count, 50);
$$;


