-- Name: consolidate_duplicate_items(jsonb); Type: FUNCTION; Schema: public; Owner: -
CREATE FUNCTION public.consolidate_duplicate_items(items_json jsonb) RETURNS jsonb
    LANGUAGE plpgsql IMMUTABLE
    SET search_path TO 'public'
    AS $$
DECLARE
  result jsonb := '[]'::jsonb;
  item_record record;
BEGIN
  -- Agrupar por SKU y movement_type, sumando cantidades
  FOR item_record IN
    SELECT 
      item->>'sku' as sku,
      item->>'movement_type' as movement_type,
      SUM((item->>'quantity')::int) as cantidad_total
    FROM jsonb_array_elements(items_json) as item
    GROUP BY item->>'sku', item->>'movement_type'
  LOOP
    result := result || jsonb_build_object(
      'sku', item_record.sku,
      'quantity', item_record.cantidad_total,
      'movement_type', item_record.movement_type
    );
  END LOOP;
  
  RETURN result;
END;
$$;


