-- Name: create_notification(character varying, text, text, text, jsonb, text, integer); Type: FUNCTION; Schema: public; Owner: -
CREATE FUNCTION public.create_notification(p_user_id character varying, p_type text, p_title text, p_body text, p_data jsonb DEFAULT '{}'::jsonb, p_dedup_key text DEFAULT NULL::text, p_expires_in_hours integer DEFAULT NULL::integer) RETURNS uuid
    LANGUAGE plpgsql SECURITY DEFINER
    SET search_path TO 'public'
    AS $$
DECLARE
    v_notification_id UUID;
    v_expires_at TIMESTAMPTZ;
BEGIN
    -- Calculate expiration if provided
    IF p_expires_in_hours IS NOT NULL THEN
        v_expires_at := NOW() + (p_expires_in_hours || ' hours')::INTERVAL;
    END IF;

    -- Insert notification (with dedup if key provided)
    IF p_dedup_key IS NOT NULL THEN
        INSERT INTO notifications (
            user_id, type, title, body, data, dedup_key, expires_at
        ) VALUES (
            p_user_id, p_type, p_title, p_body, p_data, p_dedup_key, v_expires_at
        )
        ON CONFLICT (user_id, dedup_key) WHERE dedup_key IS NOT NULL DO NOTHING
        RETURNING id INTO v_notification_id;
    ELSE
        INSERT INTO notifications (
            user_id, type, title, body, data, expires_at
        ) VALUES (
            p_user_id, p_type, p_title, p_body, p_data, v_expires_at
        )
        RETURNING id INTO v_notification_id;
    END IF;

    -- Push notification is now handled via database webhook on INSERT
    -- No need for pgmq queue

    RETURN v_notification_id;
END;
$$;


