-- Name: deduplicate_saga_items(jsonb); Type: FUNCTION; Schema: public; Owner: -
CREATE FUNCTION public.deduplicate_saga_items(items_array jsonb) RETURNS jsonb
    LANGUAGE plpgsql
    SET search_path TO 'public'
    AS $$
DECLARE
  result jsonb;
BEGIN
  -- Agrupa por SKU y movement_type, sumando cantidades
  SELECT jsonb_agg(
    jsonb_build_object(
      'sku', sku,
      'movement_type', movement_type,
      'quantity', total_cantidad
    )
  )
  INTO result
  FROM (
    SELECT 
      item->>'sku' as sku,
      item->>'movement_type' as movement_type,
      SUM((item->>'quantity')::int) as total_cantidad
    FROM jsonb_array_elements(items_array) as item
    GROUP BY item->>'sku', item->>'movement_type'
  ) aggregated;
  
  RETURN result;
END;
$$;


