-- Name: v_churn_metrics; Type: VIEW; Schema: analytics; Owner: -
CREATE VIEW analytics.v_churn_metrics AS
 SELECT date_trunc('month'::text, changed_at) AS month,
    count(*) FILTER (WHERE (new_status = 'INACTIVE'::public.client_status)) AS churns,
    count(*) FILTER (WHERE ((new_status = 'ACTIVE'::public.client_status) AND (previous_status = 'INACTIVE'::public.client_status))) AS reactivations,
    count(*) FILTER (WHERE (new_status = 'DOWNGRADING'::public.client_status)) AS marked_downgrading,
    count(*) FILTER (WHERE (new_status = 'SUSPENDED'::public.client_status)) AS suspensions,
    ( SELECT count(*) AS count
           FROM public.clients
          WHERE (clients.status = 'ACTIVE'::public.client_status)) AS current_active,
    ( SELECT count(*) AS count
           FROM public.clients
          WHERE (clients.status = 'INACTIVE'::public.client_status)) AS current_inactive
   FROM public.client_status_log
  GROUP BY (date_trunc('month'::text, changed_at))
  ORDER BY (date_trunc('month'::text, changed_at)) DESC;


--
-- Name: v_client_active_time; Type: VIEW; Schema: analytics; Owner: -
CREATE VIEW analytics.v_client_active_time AS
 WITH periods AS (
         SELECT cel.client_id,
            cel.new_status,
            cel.changed_at,
            lead(cel.changed_at) OVER (PARTITION BY cel.client_id ORDER BY cel.changed_at) AS next_change
           FROM public.client_status_log cel
        )
 SELECT p.client_id,
    c.client_name,
    c.status AS current_status,
    (sum(
        CASE
            WHEN (p.new_status = 'ACTIVE'::public.client_status) THEN EXTRACT(day FROM (COALESCE(p.next_change, now()) - p.changed_at))
            ELSE (0)::numeric
        END))::integer AS total_active_days,
    count(*) FILTER (WHERE (p.new_status = 'ACTIVE'::public.client_status)) AS times_activated,
    count(*) FILTER (WHERE (p.new_status = 'INACTIVE'::public.client_status)) AS times_churned,
    min(p.changed_at) FILTER (WHERE (p.new_status = 'ACTIVE'::public.client_status)) AS first_activation,
    max(p.changed_at) FILTER (WHERE (p.new_status = 'INACTIVE'::public.client_status)) AS last_churn
   FROM (periods p
     JOIN public.clients c ON (((c.client_id)::text = (p.client_id)::text)))
  GROUP BY p.client_id, c.client_name, c.status;


