--
-- Name: v_visit_tasks_operational; Type: VIEW; Schema: public; Owner: -
CREATE VIEW public.v_visit_tasks_operational AS
 SELECT (task_id)::text AS task_id,
    visit_id,
    task_type,
    status,
    required,
    created_at,
    started_at,
    completed_at,
    due_at,
    last_activity_at,
    reference_table,
    reference_id,
    metadata,
    (transaction_type)::text AS transaction_type,
    step_order,
    'NOT_NEEDED'::text AS compensation_status,
    '{}'::jsonb AS input_payload,
    '{}'::jsonb AS output_result,
    NULL::jsonb AS compensation_payload,
    (gen_random_uuid())::text AS idempotency_key,
    0 AS retry_count,
    3 AS max_retries,
    NULL::text AS last_error,
    NULL::timestamp with time zone AS compensation_executed_at,
        CASE
            WHEN (status = 'COMPLETED'::public.visit_task_status) THEN 'COMPLETED'::public.visit_task_status
            WHEN (status = 'SKIPPED_M'::public.visit_task_status) THEN status
            WHEN (status = 'SKIPPED'::public.visit_task_status) THEN status
            WHEN (status = 'ERROR'::public.visit_task_status) THEN status
            WHEN ((due_at IS NOT NULL) AND (due_at < now()) AND (status <> ALL (ARRAY['COMPLETED'::public.visit_task_status, 'SKIPPED_M'::public.visit_task_status, 'SKIPPED'::public.visit_task_status]))) THEN 'DELAYED'::public.visit_task_status
            ELSE status
        END AS operational_status,
        CASE
            WHEN (task_type = 'SALE_ODV'::public.visit_task_type) THEN ( SELECT string_agg(szl.zoho_id, ', '::text ORDER BY szl.created_at) AS string_agg
               FROM (public.saga_zoho_links szl
                 JOIN public.saga_transactions st ON ((st.id = szl.id_saga_transaction)))
              WHERE ((st.visit_id = vt.visit_id) AND ((szl.type)::text = 'SALE'::text)))
            WHEN (task_type = 'ODV_CABINET'::public.visit_task_type) THEN ( SELECT string_agg(szl.zoho_id, ', '::text ORDER BY szl.created_at) AS string_agg
               FROM (public.saga_zoho_links szl
                 JOIN public.saga_transactions st ON ((st.id = szl.id_saga_transaction)))
              WHERE ((st.visit_id = vt.visit_id) AND ((szl.type)::text = 'CABINET'::text)))
            ELSE NULL::text
        END AS odv_id,
        CASE
            WHEN (task_type = 'SALE_ODV'::public.visit_task_type) THEN ( SELECT (COALESCE(sum(COALESCE(((item.value ->> 'quantity'::text))::integer, ((item.value ->> 'cantidad'::text))::integer, 0)), (0)::bigint))::integer AS "coalesce"
               FROM (public.saga_zoho_links szl
                 JOIN public.saga_transactions st ON ((st.id = szl.id_saga_transaction))),
                LATERAL jsonb_array_elements(COALESCE(szl.items, st.items)) item(value)
              WHERE ((st.visit_id = vt.visit_id) AND ((szl.type)::text = 'SALE'::text)))
            WHEN (task_type = 'ODV_CABINET'::public.visit_task_type) THEN ( SELECT (COALESCE(sum(COALESCE(((item.value ->> 'cantidad_entrada'::text))::integer, ((item.value ->> 'quantity'::text))::integer, ((item.value ->> 'cantidad'::text))::integer, 0)), (0)::bigint))::integer AS "coalesce"
               FROM (public.saga_zoho_links szl
                 JOIN public.saga_transactions st ON ((st.id = szl.id_saga_transaction))),
                LATERAL jsonb_array_elements(COALESCE(szl.items, st.items)) item(value)
              WHERE ((st.visit_id = vt.visit_id) AND ((szl.type)::text = 'CABINET'::text)))
            ELSE NULL::integer
        END AS odv_total_pieces
   FROM public.visit_tasks vt;


