-- Name: messages messages_rate; Type: POLICY; Schema: chatbot; Owner: -
CREATE POLICY messages_rate ON chatbot.messages FOR UPDATE TO authenticated USING ((((role)::text = 'assistant'::text) AND (conversation_id IN ( SELECT c.id
   FROM chatbot.conversations c
